CREATE TABLE `diseasespests` (
  `did` varchar(20) NOT NULL,
  `name` varchar(20) DEFAULT NULL,
  `comname` varchar(20) DEFAULT NULL,
  `symptoms` varchar(100) DEFAULT NULL,
